import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import {
  Container, Paper, Typography, Button, Box, Divider, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, CircularProgress, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Rating, Tooltip
} from '@mui/material';
import CancelIcon from '@mui/icons-material/Cancel';
import RateReviewIcon from '@mui/icons-material/RateReview';

function formatRupees(amount) {
  return amount ? amount.toLocaleString('en-IN', { style: 'currency', currency: 'INR' }) : '';
}

function formatDateCell(date) {
  if (!date) return '-';
  if (typeof date === 'string') return date.slice(0, 10);
  if (typeof date === 'object') {
    if (typeof date.format === 'function') return date.format('YYYY-MM-DD'); // Dayjs or Moment
    if (typeof date.toISOString === 'function') return date.toISOString().slice(0, 10);
    if (typeof date.toString === 'function') return date.toString().slice(0, 10);
  }
  return '-';
}

function Profile() {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [payments, setPayments] = useState([]);
  const [paymentsLoading, setPaymentsLoading] = useState(true);
  const [reviewDialog, setReviewDialog] = useState({ open: false, payment: null, rating: 0, review: '' });
  const [actionLoading, setActionLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      const {
        data: { session },
      } = await supabase.auth.getSession();
      if (!session?.user) {
        navigate('/login');
        return;
      }
      const { data, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', session.user.id)
        .single();
      if (error) {
        setProfile(null);
      } else {
        setProfile(data);
      }
      setLoading(false);
    };
    fetchProfile();
  }, [navigate]);

  const fetchPayments = async () => {
    setPaymentsLoading(true);
    const {
      data: { session },
    } = await supabase.auth.getSession();
    if (!session?.user) return;
    const { data, error } = await supabase
      .from('payments')
      .select('*')
      .eq('user_id', session.user.id)
      .order('payment_date', { ascending: false });
    if (!error) setPayments(data || []);
    setPaymentsLoading(false);
  };

  useEffect(() => {
    fetchPayments();
  }, []);

  const handleCancel = async (id) => {
    setActionLoading(true);
    await supabase.from('payments').update({ status: 'cancelled' }).eq('id', id);
    await fetchPayments();
    setActionLoading(false);
  };

  const openReviewDialog = (payment) => {
    setReviewDialog({
      open: true,
      payment,
      rating: payment.rating || 0,
      review: payment.review || ''
    });
  };

  const closeReviewDialog = () => {
    setReviewDialog({ open: false, payment: null, rating: 0, review: '' });
  };

  const handleReviewSubmit = async () => {
    setActionLoading(true);
    await supabase.from('payments').update({ rating: reviewDialog.rating, review: reviewDialog.review }).eq('id', reviewDialog.payment.id);
    closeReviewDialog();
    await fetchPayments();
    setActionLoading(false);
  };

  if (loading) return <Typography>Loading...</Typography>;
  if (!profile) return <Typography>Profile not found.</Typography>;

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 8 }}>
        <Paper sx={{ p: 4, mb: 4, borderRadius: 3, boxShadow: 3 }}>
          <Typography variant="h5" gutterBottom>Profile</Typography>
          <Typography variant="body1"><b>Full Name:</b> {profile.full_name}</Typography>
          <Typography variant="body1"><b>Email:</b> {profile.email}</Typography>
          <Button sx={{ mt: 2 }} variant="contained" onClick={() => navigate('/')}>Back to Home</Button>
        </Paper>
        <Divider sx={{ my: 4 }} />
        <Paper sx={{ p: 3, borderRadius: 3, boxShadow: 2 }}>
          <Typography variant="h6" gutterBottom>Booking & Payment History</Typography>
          {paymentsLoading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', py: 3 }}>
              <CircularProgress />
            </Box>
          ) : payments.length === 0 ? (
            <Typography>No bookings or payments found.</Typography>
          ) : (
            <TableContainer>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Type</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Operator</TableCell>
                    <TableCell>From</TableCell>
                    <TableCell>To</TableCell>
                    <TableCell>Date</TableCell>
                    <TableCell>Departure</TableCell>
                    <TableCell>Arrival</TableCell>
                    <TableCell>Duration</TableCell>
                    <TableCell>Price</TableCell>
                    <TableCell>Status</TableCell>
                    <TableCell>Paid On</TableCell>
                    <TableCell>Rating</TableCell>
                    <TableCell>Review</TableCell>
                    <TableCell>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {payments.map((p) => (
                    <TableRow key={p.id} sx={{ bgcolor: p.status === 'cancelled' ? '#f8d7da' : 'inherit' }}>
                      <TableCell>{p.type}</TableCell>
                      <TableCell>{p.name}</TableCell>
                      <TableCell>{p.operator}</TableCell>
                      <TableCell>{p.from}</TableCell>
                      <TableCell>{p.to}</TableCell>
                      <TableCell>{formatDateCell(p.travel_date)}</TableCell>
                      <TableCell>{p.departure}</TableCell>
                      <TableCell>{p.arrival}</TableCell>
                      <TableCell>{p.duration}</TableCell>
                      <TableCell>{formatRupees(p.amount)}</TableCell>
                      <TableCell>{p.status}</TableCell>
                      <TableCell>{new Date(p.payment_date).toLocaleString('en-IN')}</TableCell>
                      <TableCell>
                        {p.rating ? <Rating value={p.rating} readOnly size="small" /> : '-'}
                      </TableCell>
                      <TableCell>
                        {p.review ? <Typography variant="body2">{p.review}</Typography> : '-'}
                      </TableCell>
                      <TableCell>
                        <Box sx={{ display: 'flex', gap: 1 }}>
                          {p.status !== 'cancelled' && (
                            <Tooltip title="Cancel Booking">
                              <span>
                                <IconButton color="error" disabled={actionLoading} onClick={() => handleCancel(p.id)}>
                                  <CancelIcon />
                                </IconButton>
                              </span>
                            </Tooltip>
                          )}
                          {p.status !== 'cancelled' && (
                            <Tooltip title={p.rating ? 'Edit Review' : 'Add Review'}>
                              <span>
                                <IconButton color="primary" disabled={actionLoading} onClick={() => openReviewDialog(p)}>
                                  <RateReviewIcon />
                                </IconButton>
                              </span>
                            </Tooltip>
                          )}
                        </Box>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          )}
        </Paper>
      </Box>
      {/* Review Dialog */}
      <Dialog open={reviewDialog.open} onClose={closeReviewDialog}>
        <DialogTitle>Rate & Review Booking</DialogTitle>
        <DialogContent>
          <Box sx={{ mb: 2 }}>
            <Rating
              value={reviewDialog.rating}
              onChange={(_, value) => setReviewDialog((d) => ({ ...d, rating: value }))}
              size="large"
            />
          </Box>
          <TextField
            label="Review"
            fullWidth
            multiline
            minRows={2}
            value={reviewDialog.review}
            onChange={e => setReviewDialog((d) => ({ ...d, review: e.target.value }))}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={closeReviewDialog}>Cancel</Button>
          <Button variant="contained" onClick={handleReviewSubmit} disabled={actionLoading}>
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default Profile;
