import React, { useState } from 'react';
import {
  Box,
  Tabs,
  Tab,
  Typography,
  Paper,
  Button,
  Chip,
  Grid,
  Container
} from '@mui/material';
import DirectionsBusIcon from '@mui/icons-material/DirectionsBus';
import TrainIcon from '@mui/icons-material/Train';
import FlightIcon from '@mui/icons-material/Flight';
import AcUnitIcon from '@mui/icons-material/AcUnit';
import HotelIcon from '@mui/icons-material/Hotel';
import WaterDropIcon from '@mui/icons-material/WaterDrop';
import WifiIcon from '@mui/icons-material/Wifi';
import PowerIcon from '@mui/icons-material/Power';
import RestaurantIcon from '@mui/icons-material/Restaurant';

const SearchResults = ({ from, to, date }) => {
  const [activeTab, setActiveTab] = useState('all');

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const transportOptions = [
    {
      type: 'bus',
      icon: <DirectionsBusIcon sx={{ fontSize: 40, color: '#3b82f6' }} />,
      title: 'Bus',
      approxTime: 'Approx. 8h journey',
      priceRange: '₹600 - ₹800'
    },
    {
      type: 'train',
      icon: <TrainIcon sx={{ fontSize: 40, color: '#22c55e' }} />,
      title: 'Train',
      approxTime: 'Approx. 10h journey',
      priceRange: '₹450 - ₹1200'
    },
    {
      type: 'flight',
      icon: <FlightIcon sx={{ fontSize: 40, color: '#a855f7' }} />,
      title: 'Flight',
      approxTime: 'Approx. 1.5h journey',
      priceRange: '₹3800 - ₹4500'
    }
  ];

  const travelOptions = [
    {
      type: 'bus',
      name: 'Express Deluxe',
      operator: 'State Express',
      departure: '08:00',
      arrival: '16:00',
      duration: '8h 0m',
      price: 600,
      features: ['AC', 'Sleeper', 'Water Bottle', 'Charging Point'],
      icons: {
        'AC': <AcUnitIcon />,
        'Sleeper': <HotelIcon />,
        'Water Bottle': <WaterDropIcon />,
        'Charging Point': <PowerIcon />
      }
    },
    {
      type: 'bus',
      name: 'Luxury AC',
      operator: 'Private Travels',
      departure: '21:00',
      arrival: '05:00',
      duration: '8h 0m',
      price: 800,
      features: ['AC', 'Sleeper', 'Blanket', 'Charging Point', 'WiFi'],
      icons: {
        'AC': <AcUnitIcon />,
        'Sleeper': <HotelIcon />,
        'Blanket': <HotelIcon />,
        'Charging Point': <PowerIcon />,
        'WiFi': <WifiIcon />
      }
    },
    {
      type: 'train',
      name: 'Godavari Express',
      operator: 'Indian Railways',
      departure: '19:30',
      arrival: '06:15',
      duration: '10h 45m',
      price: 450,
      features: ['Sleeper Class', 'Food Available', 'Charging Point'],
      icons: {
        'Sleeper Class': <HotelIcon />,
        'Food Available': <RestaurantIcon />,
        'Charging Point': <PowerIcon />
      }
    }
  ];

  const filteredOptions = activeTab === 'all' 
    ? travelOptions 
    : travelOptions.filter(option => option.type === activeTab);

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      {/* Transport Type Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        {transportOptions.map((option) => (
          <Grid item xs={12} sm={6} md={4} key={option.type}>
            <Paper
              elevation={1}
              sx={{
                p: 3,
                display: 'flex',
                alignItems: 'center',
                gap: 2,
                borderRadius: 2,
                cursor: 'pointer',
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'translateY(-2px)',
                }
              }}
              onClick={() => setActiveTab(option.type)}
            >
              <Box sx={{
                width: 56,
                height: 56,
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: option.type === 'bus' ? '#eff6ff' : 
                               option.type === 'train' ? '#f0fdf4' : '#faf5ff'
              }}>
                {option.icon}
              </Box>
              <Box>
                <Typography variant="h6" gutterBottom>
                  {option.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {option.approxTime}
                </Typography>
                <Typography variant="subtitle1" fontWeight="bold">
                  {option.priceRange}
                </Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>

      {/* Tabs */}
      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={activeTab} onChange={handleTabChange}>
          <Tab label="All Options" value="all" />
          <Tab label="Bus" value="bus" />
          <Tab label="Train" value="train" />
          <Tab label="Flight" value="flight" />
        </Tabs>
      </Box>

      {/* Travel Options */}
      <Box sx={{ mb: 4 }}>
        {filteredOptions.map((option, index) => (
          <Paper
            key={index}
            elevation={1}
            sx={{
              p: 3,
              mb: 2,
              borderRadius: 2,
            }}
          >
            <Grid container spacing={2} alignItems="center">
              {/* Transport Type */}
              <Grid item xs={12} sm={3}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Chip 
                    label={option.type.charAt(0).toUpperCase() + option.type.slice(1)}
                    color={option.type === 'bus' ? 'primary' : 
                           option.type === 'train' ? 'success' : 'secondary'}
                    size="small"
                  />
                  <Typography variant="h6">{option.name}</Typography>
                </Box>
                <Typography variant="body2" color="text.secondary">
                  {option.operator}
                </Typography>
              </Grid>

              {/* Time Details */}
              <Grid item xs={12} sm={4}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <Box>
                    <Typography variant="h6">{option.departure}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Departure
                    </Typography>
                  </Box>
                  <Box sx={{ flex: 1, textAlign: 'center', color: 'text.secondary' }}>
                    <Typography variant="body2">{option.duration}</Typography>
                  </Box>
                  <Box>
                    <Typography variant="h6">{option.arrival}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Arrival
                    </Typography>
                  </Box>
                </Box>
              </Grid>

              {/* Features */}
              <Grid item xs={12} sm={3}>
                <Typography variant="subtitle2" gutterBottom>Features:</Typography>
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                  {option.features.map((feature, idx) => (
                    <Chip
                      key={idx}
                      icon={option.icons[feature]}
                      label={feature}
                      size="small"
                      variant="outlined"
                    />
                  ))}
                </Box>
              </Grid>

              {/* Price and Book Button */}
              <Grid item xs={12} sm={2}>
                <Box sx={{ textAlign: 'right' }}>
                  <Typography variant="h6" gutterBottom>
                    ₹{option.price}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    per person
                  </Typography>
                  <Button
                    variant="contained"
                    fullWidth
                    sx={{
                      mt: 1,
                      bgcolor: '#1a73e8',
                      '&:hover': {
                        bgcolor: '#1557b0'
                      }
                    }}
                  >
                    Book Now
                  </Button>
                </Box>
              </Grid>
            </Grid>
          </Paper>
        ))}
      </Box>
    </Container>
  );
};

export default SearchResults;
