import React, { useState } from 'react';
import {
  Box,
  Container,
  TextField,
  Button,
  Grid,
  Typography,
  Paper,
  InputAdornment,
  Autocomplete,
  Alert,
  Snackbar
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import SearchResults from '../components/SearchResults';
import dayjs from 'dayjs';

const Home = () => {
  // Cities data
  const cities = [
    'Hyderabad',
    'Bangalore',
    'Chennai',
    'Mumbai',
    'Delhi',
    'Kolkata',
    'Pune',
    'Ahmedabad'
  ];

  const [searchParams, setSearchParams] = useState({
    from: '',
    to: '',
    date: dayjs()
  });
  const [showResults, setShowResults] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = () => {
    if (searchParams.from === searchParams.to) {
      setError('Source and destination cannot be the same!');
      setShowResults(false);
      return;
    }
    setError('');
    setShowResults(true);
  };

  const handleAddressChange = (type, newValue) => {
    setSearchParams({ ...searchParams, [type]: newValue });
    if (error) {
      setError('');
    }
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Box
        sx={{
          background: showResults ? '#f5f5f5' : 'linear-gradient(135deg, #1a73e8 0%, #0d47a1 100%)',
          minHeight: '100vh',
          pt: { xs: 4, md: 12 },
          pb: { xs: 6, md: 12 },
        }}
      >
        <Container maxWidth="lg">
          {!showResults && (
            <Box sx={{ textAlign: 'center', color: 'white', mb: { xs: 4, md: 8 } }}>
              <Typography 
                variant="h2" 
                component="h1" 
                gutterBottom 
                fontWeight="bold"
                sx={{
                  fontSize: { xs: '2rem', sm: '2.5rem', md: '3.5rem' },
                  mb: 3
                }}
              >
                Compare & Book Travel Tickets
              </Typography>
              <Typography 
                variant="h5" 
                sx={{ 
                  opacity: 0.9,
                  fontSize: { xs: '1rem', sm: '1.25rem', md: '1.5rem' },
                  maxWidth: '800px',
                  margin: '0 auto'
                }}
              >
                Find the best deals on bus, train, and flight tickets all in one place
              </Typography>
            </Box>
          )}

          <Paper
            elevation={3}
            sx={{
              p: { xs: 2, sm: 3, md: 4 },
              borderRadius: 2,
              backgroundColor: 'rgba(255, 255, 255, 0.98)',
              maxWidth: showResults ? 'none' : '1000px',
              margin: '0 auto',
              boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            }}
          >
            <form onSubmit={handleSearch}>
              <Grid container spacing={3}>
                <Grid item xs={12} md={4}>
                  <Typography variant="subtitle1" gutterBottom sx={{ ml: 1, fontWeight: 500 }}>
                    From
                  </Typography>
                  <Autocomplete
                    options={cities}
                    value={searchParams.from}
                    onChange={(event, newValue) => {
                      handleAddressChange('from', newValue);
                    }}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        fullWidth
                        placeholder="Enter city"
                        InputProps={{
                          ...params.InputProps,
                          startAdornment: (
                            <InputAdornment position="start">
                              <LocationOnIcon color="primary" />
                            </InputAdornment>
                          ),
                        }}
                        sx={{
                          '& .MuiOutlinedInput-root': {
                            borderRadius: 2,
                            backgroundColor: '#f8faff',
                          },
                        }}
                      />
                    )}
                  />
                </Grid>

                <Grid item xs={12} md={4}>
                  <Typography variant="subtitle1" gutterBottom sx={{ ml: 1, fontWeight: 500 }}>
                    To
                  </Typography>
                  <Autocomplete
                    options={cities}
                    value={searchParams.to}
                    onChange={(event, newValue) => {
                      handleAddressChange('to', newValue);
                    }}
                    renderInput={(params) => (
                      <TextField
                        {...params}
                        fullWidth
                        placeholder="Enter city"
                        InputProps={{
                          ...params.InputProps,
                          startAdornment: (
                            <InputAdornment position="start">
                              <LocationOnIcon color="primary" />
                            </InputAdornment>
                          ),
                        }}
                        sx={{
                          '& .MuiOutlinedInput-root': {
                            borderRadius: 2,
                            backgroundColor: '#f8faff',
                          },
                        }}
                      />
                    )}
                  />
                </Grid>

                <Grid item xs={12} md={4}>
                  <Typography variant="subtitle1" gutterBottom sx={{ ml: 1, fontWeight: 500 }}>
                    Date
                  </Typography>
                  <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DatePicker
                      value={searchParams.date}
                      onChange={(newValue) => setSearchParams({ ...searchParams, date: newValue })}
                      slotProps={{
                        textField: {
                          fullWidth: true,
                          InputProps: {
                            startAdornment: (
                              <InputAdornment position="start">
                                <CalendarTodayIcon color="primary" />
                              </InputAdornment>
                            ),
                          },
                          sx: {
                            '& .MuiOutlinedInput-root': {
                              borderRadius: 2,
                              backgroundColor: '#f8faff',
                            },
                          },
                        },
                      }}
                    />
                  </LocalizationProvider>
                </Grid>

                <Grid item xs={12}>
                  <Button
                    type="submit"
                    variant="contained"
                    fullWidth
                    size="large"
                    sx={{
                      mt: 2,
                      height: 56,
                      borderRadius: 2,
                      fontSize: '1.1rem',
                      textTransform: 'none',
                    }}
                  >
                    Search Tickets
                  </Button>
                </Grid>
              </Grid>
            </form>
          </Paper>

          {showResults && (
            <SearchResults
              from={searchParams.from}
              to={searchParams.to}
              date={searchParams.date}
            />
          )}

          <Snackbar 
            open={!!error} 
            autoHideDuration={6000} 
            onClose={() => setError('')}
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
          >
            <Alert 
              onClose={() => setError('')} 
              severity="error" 
              sx={{ width: '100%' }}
            >
              {error}
            </Alert>
          </Snackbar>
        </Container>
      </Box>
    </LocalizationProvider>
  );
};

export default Home;
